import { StatusBar } from 'expo-status-bar';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import * as Linking from 'expo-linking';

import { useTranslation } from 'react-i18next';
import { AuthContextProvider } from './src/store/context/auth-context';
import DrawerNavigator from './src/components/customDrawer/drawerNavigator';

// Screens
import LoginScreen from './src/screens/LoginScreen';
// import EmailLoginScreen from './src/screens/LoginScreen/EmailLoginScreen';
// import JoinConferenceScreen from './src/screens/JoinConferenceScreen';
// import GuestLoginScreen from './src/screens/JoinConferenceScreen/GuestLoginScreen';
// import InsideConferenceScreen from './src/screens/InsideConferenceScreen';
// import IconButton from './src/components/IconButton';

import Colors from './src/constants/colors';
import './src/utils/i18n';

const Stack = createNativeStackNavigator();
const prefix = Linking.createURL('portal');

const App = () => {
  console.log(prefix);
  const { t } = useTranslation();

  const config = {
    screens: {
      UpdateAppScreen: {
        path: 'join/:roomSlug',
        parse: {
          roomSlug: (roomSlug) => `${roomSlug}`
        }
      },
    },
  };

  const linking = {
    prefixes: [prefix],
    config
  };

  return (
    <>
      <NavigationContainer linking={linking}>
        <AuthContextProvider>
          <Stack.Navigator screenOptions={{
            headerStyle: { backgroundColor: Colors.blue },
            headerTintColor: Colors.white,
            contentStyle: { backgroundColor: Colors.white },
            headerTitleAlign: 'center',
          }}
          >
            <Stack.Screen
              name="LoginScreen"
              component={LoginScreen}
              options={{
                title: 'Login',
                headerShown: false,
              }}
            />

          </Stack.Navigator>
        </AuthContextProvider>
      </NavigationContainer>
      <StatusBar style="auto" />
    </>
  );
};

export default App;


